import useInput from "../hooks/use-input";
import classes from "./BasicForm.module.css";
const BasicForm = (props) => {
  const {
    value: entereFirstNameValue,
    isValid: firstNameIsValid,
    hasError: firstNameHasError,
    valueChangeHandler: firstNameChangeHandler,
    inputBlurHandler: firstNameBlurHandler,
    reset: resetFirstName,
  } = useInput((value) => value.trim() !== "");

  const {
    value: entereLastNameValue,
    isValid: lastNameIsValid,
    hasError: lastNameHasError,
    valueChangeHandler: lastNameChangeHandler,
    inputBlurHandler: lastNameBlurHandler,
    reset: resetLastName,
  } = useInput((value) => value.trim() !== "");

  const {
    value: entereEmailValue,
    isValid: emailIsValid,
    hasError: emailHasError,
    valueChangeHandler: emailChangeHandler,
    inputBlurHandler: emailBlurHandler,
    reset: resetEmail,
  } = useInput((value) => value.includes("@"));

  const {
    value: enterAgeValue,
    isValid: ageIsValid,
    hasError: ageHasError,
    valueChangeHandler: ageChangeHandler,
    inputBlurHandler: ageBlurHandler,
    reset: resetAge,
  } = useInput((value) => value.trim() !== "");

  let formIsValid = false;
  if (firstNameIsValid && lastNameIsValid && emailIsValid && ageIsValid) {
    formIsValid = true;
  }
  const formSubmissionHandler = (e) => {
    e.preventDefault();
    resetFirstName();
    resetLastName();
    resetEmail();
    resetAge();
  };
  const firstNameClasses = firstNameHasError
    ? "form-control invalid"
    : "form-control";
  const lastNameClasses = lastNameHasError
    ? "form-control invalid"
    : "form-control";
    const emailClasses = emailHasError
    ? "form-control invalid"
    : "form-control";
    const ageClasses = ageHasError
    ? "form-control invalid"
    : "form-control";
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className="control-group">
        <div className={firstNameClasses}>
          <label htmlFor="name">First Name</label>
          <input
            type="text"
            id="name"
            onChange={firstNameChangeHandler}
            onBlur={firstNameBlurHandler}
            value={entereFirstNameValue}
          />
        </div>
        {firstNameHasError && (
          <p className={classes.invalidp}>First Name must not be empty!</p>
        )}
        <div className={lastNameClasses}>
          <label htmlFor="name">Last Name</label>
          <input
            type="text"
            id="name"
            onChange={lastNameChangeHandler}
            onBlur={lastNameBlurHandler}
            value={entereLastNameValue}
          />
        </div>
        {lastNameHasError && (
          <p className={classes.invalidp}>Last Name must not be empty!</p>
        )}
      </div>
      <div className="control-group">
        <div className="form-control">
          <label htmlFor="name">Email-Address</label>
          <input type="text" id="name" />
        </div>
        {emailHasError && (
          <p className={classes.invalidp}>Email must includes '@' !</p>
        )}
        <div className={emailClasses}>
          <label htmlFor="name">Age</label>
          <input type="number" id="name" />
        </div>
        {ageHasError && (
          <p className={classes.invalidp}>Email must includes '@' !</p>
        )}
      </div>

      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
